import React, { useEffect, useState, useCallback} from 'react';
import '../../styles/news.css';


const News = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [news, setNews] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = 3;

  // Format the current date
  const formatCurrentDate = () => {
    const monthName = new Intl.DateTimeFormat('en-US', { month: 'long' }).format(currentDate);
    const dayNumber = currentDate.getDate();
    let daySuffix;

    switch (dayNumber) {
      case 1:
      case 21:
      case 31:
        daySuffix = 'st';
        break;
      case 2:
      case 22:
        daySuffix = 'nd';
        break;
      case 3:
      case 23:
        daySuffix = 'rd';
        break;
      default:
        daySuffix = 'th';
        break;
    }

    const formattedDay = `${dayNumber}${daySuffix}`;
    const year = currentDate.getFullYear();

    return `${monthName} ${formattedDay}, ${year}`;
  };

  // Load news function
  const loadNews = () => {
    fetch('https://tronai.io/v1/m_news', {
      method: 'POST',
      headers: {
        'Accept': 'application/json'
      },
      body: ''
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setNews(prevNews => [...prevNews, ...data.data]);
        setCurrentIndex(prevIndex => prevIndex + itemsPerPage);
      })
      .catch(error => {
        console.error('Error loading data:', error);
      });
  };

  useEffect(() => {
    loadNews();
  }, []);

  const handleSeeMore = () => {
    loadNews();
  };

  return (
    <div>
      <div className="box-container">
        <div className="rounded-box">
          <div className="month-year">{formatCurrentDate()}</div>
        </div>
      </div>
      <div id="news">
        {news.slice(0, currentIndex).map((item, index) => {
          const date = new Date(item.first_published_at);
          const formattedDate = date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
          return (
            <div className="news-container" key={index}>
              <div id="news-link">
                <a href={item.link} target="_blank" rel="noopener noreferrer">
                  <div className="time">{formattedDate}</div>
                  <div className="headline">{item.title}</div>
                  <div className="news">{item.subtitle}</div>
                </a>
              </div>
            </div>
          );
        })}
        {currentIndex >= news.length && <p>No more news to load.</p>}
      </div>
      <div class="see-more">
        <button id="seeMoreBtn" onClick={handleSeeMore}>See More</button>
    </div>
    </div>
  );
};

export default News;
