import React, { useEffect, useState } from 'react';

const NewsComponent = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = 5; // Define how many items to load per page

  const loadNews = async () => {
    try {
      const response = await fetch('https://a8fb-203-12-203-2.ngrok-free.app/v1/m_news', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
        },
        body: '',
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const postData = await response.json();
      console.log(postData);

      // Check if currentIndex is within bounds
      if (currentIndex < postData.data.length) {
        const newItems = postData.data.slice(currentIndex, Math.min(currentIndex + itemsPerPage, postData.data.length));
        setNewsItems((prevItems) => [...prevItems, ...newItems]);
        setCurrentIndex((prevIndex) => prevIndex + itemsPerPage);
      } else {
        setNewsItems((prevItems) => [...prevItems, { endReached: true }]); // Indicate no more news
      }
    } catch (error) {
      console.error('Error loading data:', error);
      setNewsItems((prevItems) => [...prevItems, { error: true }]); // Indicate an error
    }
  };

  useEffect(() => {
    loadNews();
  }, []);

  return (
    <div id="news">
      {newsItems.length > 0 ? (
        newsItems.map((item, index) => {
          if (item.error) {
            return <p key={index}>Error loading data.</p>;
          }
          if (item.endReached) {
            return <p key={index}>No more news to load.</p>;
          }

          const date = new Date(item.first_published_at);
          const options = { month: 'short', day: 'numeric' };
          const formattedDate = date.toLocaleDateString('en-US', options);

          return (
            <div className="news-container" key={item.title}>
              <div id="news-link">
                <a href={item.link} target="_blank" rel="noopener noreferrer">
                  <div className="time">{formattedDate}</div>
                  <div className="headline">{item.title}</div>
                  <div className="news">{item.subtitle}</div>
                </a>
              </div>
            </div>
          );
        })
      ) : (
        <p>Loading news...</p>
      )}
    </div>
  );
};

export default NewsComponent;
